generated.css:
:root{
    --blue_1: #042127;
    --blue_2: #023045;
    --blue_3: #134B70;
    --grey_1: #6C859D;
    --grey_2: #A9B9CF
}


*{
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html{
    font-size: 62.5%;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

header{
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    height: 5rem;
    font-size: 1.6rem;
    background: linear-gradient(var(--blue_3),var(--blue_1));
}

header .logo{
    margin-left: 15px;
}

.header-nav{
    margin-right: 15px;
    list-style: none;
}

.header-nav__options > li{
    display: inline-block;
}

.header-nav__options a{
    text-decoration: none;
    color: white;
}

.header-nav__options a::after{
    content: " | ";
}

.container .movie{
    width: 100%;
}

.container button{
    display: inline-block;
    width: 150px;
    height: 48px;
    margin: 15px auto;
    border: none;
    border-radius: 15px;
    outline: none;
    color: white;
    background-color: var(--blue_3);
    font-size: 1.6rem;
}
